import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-dssefa-alert',
  templateUrl: './dssefa-alert.component.html',
  styleUrls: ['./dssefa-alert.component.scss']
})

export class DssefaAlertComponent {
  @Input() icon: string = '<mat-icon>edit</mat-icon>';
  @Input() title: string = 'Title'; // Set default value
  @Input() description: string = 'Description: isso deve ser substituído pelo texto descritivo do card'; // Set default value
  @Input() cta: string = ''; // Default empty string
  @Input() ctaVisibility: boolean = true;
  @Input() variant: 'success' | 'warning' | 'attention' |'info'  = 'info';
  constructor() {}
}

<div class="alert" [ngClass]="{
    'warning': variant === 'warning',
    'success': variant === 'success',
    'attention': variant === 'attention',
    'info': variant ==='info',
  }">
    <div class="header">
        <div class="icon"><mat-icon>{{ icon }}</mat-icon></div>
        <div class="title-container">{{ title }}</div>
        <div class="icon-close"><mat-icon>close</mat-icon></div>
    </div>
    <div class="description">{{ description }}</div>
    <div class="cta">{{ cta }}</div>
</div>>
